mapper.update:
UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
     , CHG_ID = #{reg_id}
     , CHG_DTTM = NOW()
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_BOARD
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

mapper.delete:

       DELETE 
         FROM TBL_BOARDFILE
        WHERE FILENO IN (
             
                    ${item}
               
       )
