mapper.select:
SELECT COUNT(*)
  FROM TBL_BOARD
 
 AND REG_ID =#{id}

mapper.update:
UPDATE TBL_NOTICE
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
     , BRDDATE =  NOW()
     , CHG_ID = #{reg_id}
     , CHG_DTTM = NOW()
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_BOARD
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
     , BRDDATE =  NOW()             
     , CHG_ID = #{reg_id}
     , CHG_DTTM = NOW()
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_ISSUE
   SET BRDTITLE=#{brdtitle}
     , BRDWRITER=#{brdwriter}
     , BRDMEMO=#{brdmemo} 
     , BRDDATE =  NOW()             
     , CHG_ID = #{reg_id}
     , CHG_DTTM = NOW()
 WHERE BRDDELETEFLAG='N'
   AND BRDNO=#{brdno}

UPDATE TBL_NOTICE
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

UPDATE TBL_BOARD
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

UPDATE TBL_ISSUE
   SET BRDHIT = BRDHIT + 1 
 WHERE BRDNO=#{brdno}

mapper.delete:

       DELETE 
         FROM TBL_BOARDFILE
        WHERE FILENO IN (
             
                    ${item}
               
       )

mapper.select:
SELECT COUNT(*) 
  FROM TBL_LIKES 
 WHERE BRDNO = #{brdno}
   AND USER_ID = #{user_id}

SELECT COUNT(*) 
  FROM TBL_LIKES 
 WHERE BRDNO = #{brdno}

mapper.delete:

       DELETE FROM TBL_LIKES
        WHERE USER_ID= #{user_id}    
          AND BRDNO = #{brdno}
